function smoothScroll(t,e,o=100){const c=window.top.pageYOffset,l=Math.abs(c-t);let n=Math.ceil(l/e*o)+.05*o;if(currItem==t&&n<l){c>t+n&&(n=-n);const o=c+window.innerHeight;o+n<e?requestAnimationFrame(()=>{scrollBy(0,n),smoothScroll(t,e)}):e-o<e-t&&scrollBy(0,e-o)}}function scroll(t){const e=t.offsetTop-t.offsetHeight;currItem=e;smoothScroll(e,document.body.clientHeight)}let currItem;const navItems=Array.from(document.querySelectorAll('[href^="#"]'));navItems.map(t=>{t.onclick=function(e){e.preventDefault();scroll(document.getElementById(t.getAttribute("href").slice(1)))}});